import React from 'react';
import { useNavigate } from 'react-router-dom';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import IconButton from '@mui/material/IconButton';
import LogoutIcon from '@mui/icons-material/Logout';
import Typography from '@mui/material/Typography';
import styles from './styles.module.css';

const Home = () => {
  const navigate = useNavigate();

  const goToMyIssues = () => {
    navigate('/my-issues');
  };

  const goToSearch = (category) => {
    if (category === 'forums') {
      navigate('/FSearch');
    } else if (category === 'reviews') {
      navigate('/RSearch');
    }
  };

  const handleLogout = () => {
    sessionStorage.removeItem('token');
    window.location.href = '/';
  };

  const cardActions = {
    'My Issues': goToMyIssues,
    'Forums': () => goToSearch('forums'),
    'Reviews': () => goToSearch('reviews')
  };

  return (
    <div className={styles.wrapper}>
      <div className={styles.logout_btn}>
        <IconButton
          onClick={handleLogout}
          style={{ color: 'red', fontSize: '30px' }}
        >
          <LogoutIcon fontSize="inherit" />
        </IconButton>
      </div>

      <div className={styles.cardsContainer}>
        {['My Issues', 'Forums', 'Reviews'].map((title, index) => (
          <Card
            key={index}
            className={`${styles.card} ${styles.smallCard}`}
            onClick={cardActions[title]}
            style={{ cursor: 'pointer' }} // Change cursor to pointer
          >
            <CardContent className={styles.cardContent}>
              <Typography variant="h5" component="div" style={{ color: 'white' }}>
                {title}
              </Typography>
            </CardContent>
          </Card>
        ))}
      </div>

      <footer className={styles.footer}>
        &copy; {new Date().getFullYear()} Meltwater. All rights reserved.
      </footer>
    </div>
  );
};

export default Home;
