import React, { useState, useEffect } from 'react';
import axios from 'axios';
import IconButton from '@mui/material/IconButton';
import LogoutIcon from '@mui/icons-material/Logout';
import styles from './styles.module.css'; // Assuming you have a CSS module file
import HomeIcon from '@mui/icons-material/Home';

const MyIssues = () => {
    const [issuelst, setIssuelst] = useState([]);
    const [isFetching, setIsFetching] = useState(false);
    const [issuesFetched, setIssuesFetched] = useState(false);

    const handleLogout = () => {
        sessionStorage.removeItem('token');
        window.location.href = '/';
    };

    useEffect(() => {
        setIsFetching(true);
        axios.get(`${process.env.REACT_APP_BACKEND_URL}/gettickets`)
            .then(response => {
                setIssuelst(response.data);
                setIsFetching(false);
                setIssuesFetched(true);
            })
            .catch(error => {
                console.error('Error fetching issues:', error);
                setIsFetching(false);
                setIssuesFetched(true);
            });
    }, []);

    return (
        <div>
            <div className={styles.homeButton}>
                <IconButton onClick={() => window.history.back()} style={{ color: 'white', fontSize: '30px' }}>
                    <HomeIcon fontSize="inherit" />
                </IconButton>
            </div>
            <div className={styles.header}>
                    <div className={styles.logout_btn}>
                        <IconButton onClick={handleLogout} style={{ color: 'red', fontSize: '30px' }}>
                            <LogoutIcon fontSize="inherit" />
                        </IconButton>
                    </div>
                </div>

            <div className={styles.wrapper}>
                {/* Header with Home and Logout buttons */}


                {/* Main content */}
                <div className={styles.content}>
                    <h1 className={styles.pageTitle}>My Issues</h1>
                    <div className={styles.issueListContainer}>
                        {isFetching && <p>Loading issues...</p>}
                        {issuesFetched && !isFetching && (
                            issuelst.length > 0 ? (
                                <ul className={styles.issueList}>
                                    {issuelst.map((issue, index) => (
                                        <li key={index} className={styles.issueItem}>
                                            <a href={`https://meltwater.atlassian.net/browse/${issue}`} target="_blank" rel="noopener noreferrer" className={styles.issueLink}>
                                                {issue}
                                            </a>
                                        </li>
                                    ))}
                                </ul>
                            ) : (
                                <p>No issues available</p>
                            )
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default MyIssues;
